(function() {
    var Ext = window.Ext4 || window.Ext;

    /**
     * A utility for bulk updating records synchronously.
     * Useful when changing multiple artifacts with the same data, e.g., when bulk reparenting, assigning state, etc.
     *
     * Necessary because concurrency conflicts can occur when firing all updates at the same time
     * asynchronously on artifacts with properties that roll up (UserStories, PortfolioItems, and Tasks).
     *
     *     @example
     *     Rally.data.BulkRecordUpdater.updateRecords({
     *         records: [record1, record2],
     *         propertiesToUpdate: {
     *             Parent: '/hierarchicalrequirement/123.js'
     *         },
     *         success: function(readOnlyRecords){
     *             //all updates finished, except for given read only records
     *         },
     *         scope: this
     *     });
     *
     */

    Ext.define('Rally.data.BulkRecordUpdater', {
        singleton: true,

        /**
         * @param options
         * @param options.propertiesToUpdate An object of name/value pairs to update on each record.
         * For example:
         *     {Parent: '/hierarchicalrequirement/123.js'}
         * @param options.records List of Rally.domain.WsapiModel records to update with propertiesToUpdate
         * @param options.success Function called when all records are updated.
         * @param options.recordUpdate Function called once per record that is updated.
         * @param options.scope Scope to call the success function with.
         * @param options.success.notUpdatedRecords a list of records that were not updated because the user does not have update privileges, or for some other wsapi failure.
         * @param options.recordUpdate.record {Rally.data.Model} The successfully updated record
         */
        updateRecords: function(options) {
            options.notUpdatedRecords = options.notUpdatedRecords || [];
            if (options.records.length) {

                var record = options.records.pop();

                if (!record.get('updatable') && !record.phantom) {
                    options.notUpdatedRecords.push(record);
                    return this.updateRecords(options);
                }
                if(options.propertiesToUpdate){
                    record.set(options.propertiesToUpdate);
                }
                record.save({
                    callback: function(records, operation) {
                        if(operation.wasSuccessful()){
                            if (Ext.isFunction(options.recordUpdate)) {
                                options.recordUpdate.call(options.scope, record);
                            }
                        } else {
                            record.errorMessages = operation.error.errors;
                            options.notUpdatedRecords.push(record);
                        }
                        this.updateRecords(options);
                    },
                    scope: this
                });
            } else {
                options.success.call(options.scope, options.notUpdatedRecords);
            }
        }

    });
})();



